import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { AlignCenterVertical as Certificate, ExternalLink } from 'lucide-react';

const categories = ['All', 'Web Development', 'Cloud', 'Networking' ];

const certifications = [
  {
    title: 'CS50s Introduction to Computer Science',
    organization: 'Harvard University',
    date: 'March 2024',
    category: 'Computer Sceince',
    credentialUrl: 'https://certificates.cs50.io/919dc2f7-d71d-4777-98c3-8d7e50ed3491.pdf?size=letter',
    image: 'data:image/jpeg;base64,',
  },
  {
    title: 'JavaScript Essentials 1',
    organization: 'CISCO Networking Academy',
    date: 'December 2024',
    category: 'Web Development',
    credentialUrl: 'https://www.credly.com/badges/79f3b459-a1c6-402b-90d8-d4512c2c6917/public_url',
    image: 'data:image/jpeg;base64,',
  },
  {
    title: 'JavaScript Essentials 2',
    organization: 'CISCO Networking Academy',
    date: 'December 2024',
    category: 'Web Development',
    credentialUrl: 'https://www.credly.com/badges/ccdfa2cc-28e3-4860-a625-5580c16f30fd/public_url',
    image: 'data:image/jpeg;base64,',
  },
  {
    title : 'Cloud Computing',
    organization : 'NPTEL',
    date : 'October 2024',
    category : 'Cloud',
    credentialUrl : 'https://archive.nptel.ac.in/content/noc/NOC24/SEM2/Ecertificates/106/noc24-cs118/Course/NPTEL24CS118S105680024303971079.pdf',
    image : 'data:image/png;base64,'
  },
  {
    title: 'CCNA: Introduction to Networks',
    organization: 'CISCO Networking Academy',
    date: 'August 2024',
    category: 'Networking',
    credentialUrl: 'https://www.credly.com/badges/23cc4c2b-61e1-4979-99b9-1ac16203488b/public_url',
    image: 'data:image/jpeg;base64,',
  },
  
];

const Certifications = () => {
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const filteredCertifications = certifications.filter(
    cert => selectedCategory === 'All' || cert.category === selectedCategory
  );

  return (
    <section id="certifications" className="py-20 bg-white dark:bg-gray-900">
      <div className="container mx-auto px-4">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 20 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.5 }}
          className="text-center mb-12"
        >
          <h2 className="text-4xl font-bold text-gray-900 dark:text-white mb-4">
            Certifications
          </h2>
          <p className="text-lg text-gray-600 dark:text-gray-300">
            Professional certifications and achievements
          </p>
        </motion.div>

        <div className="flex justify-center gap-4 mb-12 flex-wrap">
          {categories.map((category) => (
            <button
              key={category}
              onClick={() => setSelectedCategory(category)}
              className={`px-4 py-2 rounded-full transition-colors ${
                selectedCategory === category
                  ? 'bg-blue-600 text-white'
                  : 'bg-gray-100 dark:bg-gray-800 text-gray-600 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700'
              }`}
            >
              {category}
            </button>
          ))}
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {filteredCertifications.map((cert, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 20 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              className="bg-white dark:bg-gray-800 rounded-lg shadow-lg overflow-hidden hover:shadow-xl transition-shadow"
            >
              <img
                src={cert.image}
                alt={cert.title}
                className="w-full h-48 object-cover"
              />
              <div className="p-6">
                <div className="flex items-start justify-between">
                  <div>
                    <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-2">
                      {cert.title}
                    </h3>
                    <p className="text-gray-600 dark:text-gray-300 mb-1">
                      {cert.organization}
                    </p>
                    <p className="text-sm text-blue-600 dark:text-blue-400">
                      {cert.date}
                    </p>
                  </div>
                  <Certificate className="w-6 h-6 text-blue-500" />
                </div>
                <a
                  href={cert.credentialUrl}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="mt-4 inline-flex items-center text-blue-600 dark:text-blue-400 hover:underline"
                >
                  View Credential
                  <ExternalLink className="w-4 h-4 ml-1" />
                </a>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Certifications;